import React, { useEffect, useState } from 'react';
import { Table, Grid, Button } from 'semantic-ui-react';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import { useSubstrate } from './substrate-lib';

import { Abi, ContractPromise } from '@polkadot/api-contract';
import metadata from './erc20/metadata.json';

export default function Main (props) {
  const { api, keyring } = useSubstrate();
  const accounts = keyring.getPairs();
  const [balances, setBalances] = useState({});

  // ERC20 Contract address as deployed on local node
  const ERC20 = '5HoRXu5vGUjQARwNCD8Gpf8acxSgs1AWX1WBWTfFTU7GhsuJ';
  const abi = new Abi(metadata);
  const apiContract = new ContractPromise(api, abi, ERC20);

  const gasLimit = 3000n * 1000000n;

  const balanceOf = async (who) => {
    console.log(who)
    const result = await apiContract.query.balanceOf(who, 0, gasLimit, who);

    console.log(result)
    // return result.output.toString();
  };

  const updateBalances = (addresses) => {
    const balancesMap = Object.keys(addresses).reduce((acc, address) => ({
      ...acc, [address]: balanceOf(addresses[address])
    }), {});

    setBalances(balancesMap);
  };

  useEffect(() => {
    const addresses = keyring.getPairs().map(account => account.address);
    let unsubscribeAll = null;

    api.query.system.account
      .multi(addresses, balances => {
        updateBalances(addresses)
      }).then(unsub => {
        unsubscribeAll = unsub;
      }).catch(console.error);

    return () => unsubscribeAll && unsubscribeAll();
  }, [api, keyring, setBalances]);

  return (
    <Grid.Column>
      <h1>Balances ERC20</h1>
      <Table celled striped size='small'>
        <Table.Body>{accounts.map(account =>
          <Table.Row key={account.address}>
            <Table.Cell width={3} textAlign='right'>{account.meta.name}</Table.Cell>
            <Table.Cell width={10}>
              <span style={{ display: 'inline-block', minWidth: '31em' }}>
                {account.address}
              </span>
              <CopyToClipboard text={account.address}>
                <Button
                  basic
                  circular
                  compact
                  size='mini'
                  color='blue'
                  icon='copy outline'
                />
              </CopyToClipboard>
            </Table.Cell>
            <Table.Cell width={3}>{
              balances && balances[account.address] &&
              balances[account.address]
            }</Table.Cell>
          </Table.Row>
        )}
        </Table.Body>
      </Table>
    </Grid.Column>
  );
}
