import React, { useEffect, useState } from 'react';
import { Grid, Modal, Button, Card } from 'semantic-ui-react';

import { useSubstrate } from './substrate-lib';

function Main (props) {
  const { api } = useSubstrate();
  // const [metadata, setMetadata] = useState({ data: null, version: null });
  const [latestBlock, setLatestBlock] = useState({});

  useEffect(() => {
    const getLatestBlock = async () => {
      try {
        const block = api.rpc.chain.getBlock();
        setLatestBlock({ block })
        // setMetadata({ data, version: data.version });
      } catch (e) {
        console.error(e);
      }
    };
    getLatestBlock();
  }, [api.rpc.chain]);

  return (
    <Grid.Column>
    </Grid.Column>
  );
}

export default function LatestBlock (props) {
  const { api } = useSubstrate();
  return api.rpc && api.rpc.chain && api.rpc.chain.getBlock ? (
    <Main {...props} />
  ) : null;
}
