import React, { useEffect, useState } from 'react';
import { Table, Grid, Button } from 'semantic-ui-react';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import { useSubstrate } from './substrate-lib';

import { Abi, ContractPromise } from '@polkadot/api-contract';
import metadata from './erc20/metadata.json';

export default function Main (props) {
  const { api, keyring } = useSubstrate();
  const accounts = keyring.getPairs();
  const [balances, setBalances] = useState({});

  useEffect(() => {
    // ERC20 Contract address as deployed on local node
    const ERC20 = '5HoVabf6G2QyUEbaFUVX4yQBdaNFwUZ7MYQEkkkzxb4hV8Bm';

    const abi = new Abi(metadata);
    const apiContract = new ContractPromise(api, abi, ERC20);

    // Default accounts addresses on local node
    const addresses = {
      alice: '5GrwvaEF5zXb26Fz9rcQpDWS57CtERHpNehXCPcNoHGKutQY',
      alice_stash: '5GNJqTPyNqANBkUVMN1LPPrxXnFouWXoe2wNSmmEoLctxiZY',
      bob: '5FHneW46xGXgs5mUiveU4sbTyGBzmstUspZC92UhjJM694ty',
      bob_stash: '5HpG9w8EBLe5XCrbczpwq5TSXvedjrBGCwqxK1iQ7qUsSWFc',
      charlie: '5FLSigC9HGRKVhB9FiEo4Y3koPsNmBmLJbpXg2mp1hXcS59Y',
      dave: '5DAAnrj7VHTznn2AWBemMuyBwZWs6FNFjdyVXUeYum3PTXFy',
      eve: '5HGjWAeFDfFCWPsjFQdVV2Msvz2XtMktvgocEZcCj68kUMaw',
      ferdie: '5CiPPseXPECbkjWCa6MnjNokrgYjMqmKndv2rSnekmSK2DjL'
    };

    // Contract owner's address
    const ALICE = addresses.alice;

    const balanceOf = async (who) => {
      const result = await apiContract.call('rpc', 'balanceOf', 0, 1000000000000, who)
        .send(ALICE);
      return result.output.toString();
    };

    // const addresses = keyring.getPairs().map(account => account.address);
    let unsubscribeAll = null;

    const balancesMap = Object.keys(addresses).reduce((acc, address) => ({
      ...acc, [address]: balanceOf(address)
    }), {});

    // console.log(balancesMap)
    setBalances(balancesMap);
    // api.query.system.account
    //   .multi(addresses, balances => {
    //     const balancesMap = addresses.reduce((acc, address, index) => ({
    //       ...acc, [address]: balances[index].data.free.toHuman()
    //     }), {});
    //     setBalances(balancesMap);
    //   }).then(unsub => {
    //     unsubscribeAll = unsub;
    //   }).catch(console.error);

    return () => unsubscribeAll && unsubscribeAll();
  }, [api, keyring, setBalances]);

  return (
    <Grid.Column>
      <h1>Balances ERC20</h1>
      <Table celled striped size='small'>
        <Table.Body>{accounts.map(account =>
          <Table.Row key={account.address}>
            <Table.Cell width={3} textAlign='right'>{account.meta.name}</Table.Cell>
            <Table.Cell width={10}>
              <span style={{ display: 'inline-block', minWidth: '31em' }}>
                {account.address}
              </span>
              <CopyToClipboard text={account.address}>
                <Button
                  basic
                  circular
                  compact
                  size='mini'
                  color='blue'
                  icon='copy outline'
                />
              </CopyToClipboard>
            </Table.Cell>
            <Table.Cell width={3}>{
              balances && balances[account.address] &&
              balances[account.address]
            }</Table.Cell>
          </Table.Row>
        )}
        </Table.Body>
      </Table>
    </Grid.Column>
  );
}
